var charity = charity || {};
charity.shared = charity.shared || {};
charity.shared.cookie = function() {
    return {
        setCookie: function(c, d, b) {
            var a = new Date;
            a.setTime(a.getTime() + 864E5 * b);
            b = "expires\x3d" + a.toUTCString();
            document.cookie = c + "\x3d" + d + "; " + b + "; path\x3d/"
        },
        getCookie: function(c) {
            c += "\x3d";
            for (var d = document.cookie.split(";"), b = 0; b < d.length; b++) {
                for (var a = d[b];
                    " " == a.charAt(0);) a = a.substring(1);
                if (0 == a.indexOf(c)) return a.substring(c.length, a.length)
            }
            return ""
        }
    }
}();